function convertBloggerTimestamp(timestamp) {
  var currentTime = new Date();
  var convertedTimestamp = new Date(timestamp);
  var offset = -new Date().getTimezoneOffset();
  convertedTimestamp = new Date(convertedTimestamp.getTime() + offset * 60 * 1000);
  var timeDifference = currentTime - convertedTimestamp;

  var seconds = Math.floor(timeDifference / 1000);
  var minutes = Math.floor(seconds / 60);
  var hours = Math.floor(minutes / 60);
  var days = Math.floor(hours / 24);
  var weeks = Math.floor(days / 7);
  var months = Math.floor(days / 30.44);
  var years = Math.floor(months / 12);

  if (seconds < 60) {
  return seconds + ' ثانية';
  } else if (minutes < 60) {
  return minutes + ' دقيقة ';
  } else if (hours < 24) {
  return hours + ' ساعة';
  } else if (days < 7) {
  return days + ' ايام';
  } else if (weeks < 4) {
  return weeks + ' اسبوع';
  } else if (months < 12) {
  return months + ' شهر';
  } else {
  return years + ' عام';
  }
  }
function getCookie(c_name) {
  if (document.cookie.length > 0) {
      c_start = document.cookie.indexOf(c_name + "=");
      if (c_start != -1) {
          c_start = c_start + c_name.length + 1;
          c_end = document.cookie.indexOf(";", c_start);
          if (c_end == -1) {
              c_end = document.cookie.length;
          }
          return unescape(document.cookie.substring(c_start, c_end));
      }
  }
  return "";
 }
