$(document).ready(function() { 
 	$.urlParam = function(name){
    var results = new RegExp('[\?&]' + name + '=([^&#]*)').exec(window.location.href);
    if (results==null) {
       return null;
    }
    return decodeURI(results[1]) || 0;
} 
var page_no = $.urlParam('index'); 

document.cookie = "page_no_current="+parseInt(page_no );
//
var id = "nav_"+page_no;
$(id).addClass('active')
if(parseInt(page_no)==1){
  $('#nav_1').addClass('active')
}else if(parseInt(page_no)==2){
  $('#nav_2').addClass('active')
}
else if(parseInt(page_no)==3){
  $('#nav_3').addClass('active')
} 
else if(parseInt(page_no)==4){
  $('#nav_4').addClass('active')
} 
else if(parseInt(page_no)==5){
  $('#nav_5').addClass('active')
} 
else if(parseInt(page_no)==5){
  $('#nav_6').addClass('active')
} 


